<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd"><html xmlns="http://www.w3.org/1999/xhtml">    <head>        <title>Membership Shortcode</title>        <meta http-equiv="Content-Type" content="text/html; charset=UTF-8" />        <script language="javascript" type="text/javascript" src="../../../../../wp-includes/js/tinymce/tiny_mce_popup.js"></script>        <script language="javascript" type="text/javascript" src="../../../../../wp-includes/js/tinymce/utils/mctabs.js"></script>        <script language="javascript" type="text/javascript" src="../../../../../wp-includes/js/tinymce/utils/form_utils.js"></script>        <script language="javascript" type="text/javascript" src="../../../../../wp-includes/js/jquery/jquery.js?ver=1.4.2"></script>        <script language="javascript" type="text/javascript">            function init() {                tinyMCEPopup.resizeToInnerSize();            }	            function insertShortcode() {		                var tagtext;                // who is active ?                var membertype = document.getElementById('membertype');                var selectedContent = tinyMCE.activeEditor.selection.getContent();                                 if(membertype != ''){                    tagtext = '[private_content id='+ membertype.value +']'+selectedContent+'[/private_content]';                }                                if(window.tinyMCE) {                    window.tinyMCE.execInstanceCommand('content', 'mceInsertContent', false, tagtext);                    //Peforms a clean up of the current editor HTML.                     //tinyMCEPopup.editor.execCommand('mceCleanup');                    //Repaints the editor. Sometimes the browser has graphic glitches.                     tinyMCEPopup.editor.execCommand('mceRepaint');                    tinyMCEPopup.close();                }		                return;            }        </script>        <base target="_self" />        <style type="text/css">            label span { color: #f00; }        </style>    </head>    <body onload="init();">        <!-- <form onsubmit="insertLink();return false;" action="#"> -->        <form name="SSShortcodes" action="#">            <div class="tabs">                <ul >                </ul>            </div>            <div class="panel_wrapper">                <!-- small panel -->                <div id="small_panel" class="panel current">                    <fieldset style="padding-left:15px;">                        <legend>Shorcode</legend>                        <br/>                        <table border="0" cellpadding="4" cellspacing="0">                            <tr>                                <td nowrap="nowrap"><label for="membertype">Enter Membership Id:</label></td>                                <td>                                    <input type="text" id="membertype" name="membertype"/>                                                                    </td>                            </tr>                        </table>                         <p>Note: Put the id of your member level to protect your content. You can use only one member level id at a time.</p>                    </fieldset>                </div>                <!-- end small panel -->                <div class="mceActionPanel">                    <div style="float: left">                        <input type="button" id="cancel" name="cancel" value="Close" onclick="tinyMCEPopup.close();" />                    </div>                    <div style="float: right">                        <input type="submit" id="insert" name="insert" value="Insert" onclick="insertShortcode();" />                    </div>                </div>        </form>    </body></html>